import React from "react";
//import { Link } from "react-router-dom";

class Students extends React.Component {
  state = {
    students: []
  };

  componentDidMount() {
    this.getAllStudents();
  }

  fetchStudents = (items) => {
    let students = [];
    items.forEach(c => {
      const obj = {
          id: c.id,
          code: c.code,
          name: c.name,
          dni: c.dni,
          street: c.address.street,
          city: c.address.city,
          postalCode: c.address.postalCode,
          country: c.address.country,
      }
      students.push(obj);

    })
    this.setState({students: students})
  }

  getAllStudents() {
    fetch('http://localhost:9000/api/students')
    .then(res => res.json())
    .then((data) => {
      this.fetchStudents(data);
    })
    .catch(console.log);
  }

  render() {
    return (
        <div class="wrapper">
          <div class="alert alert-primary" role="alert">
            Students List
          </div>
          {
          this.state.contacts.map((contact) => {
            return <li key={contact.id.toString()}>
                <div vocab="https://schema.org/" typeof="Person">
                  <span property="name"> {contact.name} </span>
                  <span property="telephone"> {contact.phoneNumber} </span>
                  <Link to={`single/${contact.id}`}> See details </Link>
                </div>
            </li>
          })
        }
      </div>
    );
  }
}
export default Students;
